package com.bzboss.app.custom;import java.util.regex.Patternclass Validator {    private val HIGHT_WIGHT =        "^\\d+(\\.\\d+)?\$"    private val EMAIL_PATTERN =        "^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@" + "[A-Za-z0-9-]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})$"    fun validateTextNotNull(email: String): Boolean {        return email.trim { it <= ' ' }.isNotEmpty()    }    fun validateEmail(email: String): Boolean {        val pattern = Pattern.compile(EMAIL_PATTERN)        val matcher = pattern.matcher(email)        return matcher.matches()    }    fun validateHightWight(email: String): Boolean {        val pattern = Pattern.compile(HIGHT_WIGHT)        val matcher = pattern.matcher(email)        return matcher.matches()    }}